import React from 'react';
import './UserCard.css';

interface User {
  id: string;
  name: string;
  avatar: string;
  role: string;
}

interface UserCardProps {
  user: User;
  onEdit: (userId: string) => void;
}

/**
 * UserCard component displays user information with edit functionality
 */
export const UserCard: React.FC<UserCardProps> = ({ user, onEdit }) => {
  const handleEdit = () => {
    onEdit(user.id);
  };

  return (
    <article className="user-card" role="article" aria-label={`User card for ${user.name}`}>
      <img
        src={user.avatar}
        alt={`${user.name}'s avatar`}
        className="user-card__avatar"
        loading="lazy"
      />
      <div className="user-card__info">
        <h3 className="user-card__name">{user.name}</h3>
        <p className="user-card__role" aria-label="User role">
          {user.role}
        </p>
      </div>
      <button
        onClick={handleEdit}
        className="user-card__edit-btn"
        aria-label={`Edit ${user.name}'s profile`}
      >
        Edit
      </button>
    </article>
  );
};
```

**Usage:**
```tsx
<UserCard
  user={{ id: '1', name: 'Jane Doe', avatar: '/avatars/jane.jpg', role: 'Developer' }}
  onEdit={(userId) => console.log('Edit user:', userId)}
/>
```
